import noteContext from "./noteContext";


const NoteState = (props) => {
  const host = "http://localhost:5000";
  
  const notesinitial =[]
    
   //fetchallnotes
  const getallNotes = async () => {

    //api call
  const responce = await fetch(`${host}/api/notes/fetchallnotes`, {
    method : 'GET',
    headers: {
