    }
  });
  const json = await responce.json();
   console.log(json)
   setnotes(json)
  }

  //addnote
  const addNote = async (title, description, tag) => {

    //api call
  const responce = await fetch(`${host}/api/notes/addnote`, {
    method : 'POST',
    headers: {
      'Content-Type': 'application/json',
      'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjgzZDQ1NGQxM2IwYTVjOWNkYTUzNjM0In0sImlhdCI6MTc0ODkzMjgxNH0.Vv5T8PXX5_JsiZSWP7qNqa-msCoHUkqH1W4ZnNi4wgE'
    },
    body: JSON.stringify({title, description, tag})
  });
  const json = await responce.json();

    console.log("adding a note");
    const note = {
      "_id": "684044d45c08a43c075db3586a",
      "user": "683d454d13b0a45c9cda53634",
      "title": title,
      "description": description,
      "tag": tag,
      "date": "2025-06-04T13:06:28.287Z",
      "__v": 0
    }
    setnotes(notes.concat(note));
  }

  //deleteNote
  const deleteNote = async (id) => {
    //api call
  const responce = await fetch(`${host}/api/notes/deletenote/${id}`, {
    method : 'DELETE',
    headers: {
      'Content-Type': 'application/json',
      'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjgzZDQ1NGQxM2IwYTVjOWNkYTUzNjM0In0sImlhdCI6MTc0ODkzMjgxNH0.Vv5T8PXX5_JsiZSWP7qNqa-msCoHUkqH1W4ZnNi4wgE'
    }
  });
   const json = await responce.json();
   console.log(json);

    console.log("deleting a note with id" + id)
    const newNote = notes.filter((note) => { return note._id !== id })
    setnotes(newNote)
  }


  //editNote
  const editNote = async(id, title, description, tag) => {
  //api call
  const responce = await fetch(`${host}/api/notes/updatenote/${id}`, {
    method : 'PUT',
    headers: {
      'Content-Type': 'application/json',
      'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjgzZDQ1NGQxM2IwYTVjOWNkYTUzNjM0In0sImlhdCI6MTc0ODkzMjgxNH0.Vv5T8PXX5_JsiZSWP7qNqa-msCoHUkqH1W4ZnNi4wgE'
    },
    body: JSON.stringify({title, description, tag})
  });
  const json = await responce.json();
  console.log(json)
  
  //Logic to edit in client
   let newNotes = JSON.parse(JSON.stringify(notes))
  for (let index = 0; index < newNotes.length; index++) {
    const element = newNotes[index];
    if (element._id === id) {
      newNotes[index].title = title;
      newNotes[index].description = description;
      newNotes[index].tag = tag;
    break;
    }
  }
  
  setnotes(newNotes);
  
  }

  
  
  const [notes, setnotes] = useState(notesinitial)
  return (
    <noteContext.Provider value={{ notes, addNote, deleteNote, editNote, getallNotes }}>
      {props.children}
    </noteContext.Provider>
  )
}

export default NoteState;
