import { Link, useLocation, useNavigate } from "react-router-dom";
import { useContext } from "react";
import authContext from "../Context/notes/authContext"; // adjust path
import "./navbar.css";

const Navbar = () => {
  let location = useLocation();
  const navigate = useNavigate();

  const { user, logout } = useContext(authContext);
  const isLoggedIn = !!user; // ✅ from context

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="navbar navbar-expand-lg custom-navbar sticky-top shadow-sm">
      <div className="container-fluid">
        {/* Brand */}
        <Link
          className="navbar-brand fw-bold fs-3 text-white"
          to={isLoggedIn ? "/home" : "/login"}
        >
          <i className="bi bi-journal-text me-2"></i> iNotebook
        </Link>

        {/* Links */}
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto">
            {isLoggedIn && (
              <>
                <li className="nav-item">
                  <Link
                    className={`nav-link ${
                      location.pathname === "/home" ? "active-nav" : "text-white"
                    }`}
                    to="/home"
                  >
                    Home
                  </Link>
                </li>
                <li className="nav-item">
                  <Link
                    className={`nav-link ${
                      location.pathname === "/about" ? "active-nav" : "text-white"
                    }`}
                    to="/about"
                  >
                    About
                  </Link>
                </li>
              </>
            )}
          </ul>

          {/* Auth Section */}
          <div className="d-flex align-items-center">
            {!isLoggedIn ? (
              <>
                <Link
                  className="btn btn-outline-light rounded-pill mx-2"
                  to="/login"
                >
                  Login
                </Link>
                <Link
                  className="btn btn-warning rounded-pill mx-2"
                  to="/signup"
                >
                  Sign Up
                </Link>
              </>
            ) : (
              <div className="dropdown">
                <button
                  className="btn btn-outline-light dropdown-toggle d-flex align-items-center rounded-pill"
                  type="button"
                  data-bs-toggle="dropdown"
                >
                  <img
                    src={
                      user?.avatar ||
                      "https://cdn-icons-png.flaticon.com/512/149/149071.png"
                    }
                    alt="profile"
                    className="rounded-circle me-2"
                    width="35"
                    height="35"
                  />
                  <span>{user?.name || "User"}</span>
                </button>
                <ul className="dropdown-menu dropdown-menu-end">
                  <li className="dropdown-item text-muted">
                    <small>{user?.email}</small>
                  </li>
                  <li>
                    <hr className="dropdown-divider" />
                  </li>
                  <li>
                    <button
                      className="dropdown-item text-danger"
                      onClick={handleLogout}
                    >
                      Logout
                    </button>
                  </li>
                </ul>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
