import React, { useContext, useEffect, useRef, useState } from 'react';
import noteContext from '../Context/notes/noteContext';
import Noteitem from './Noteitem';
import AddNote from './AddNote';

const Notes = () => {
  const context = useContext(noteContext);
  const { notes, getallNotes, editNote } = context;

  useEffect(() => {
    getallNotes();
    // eslint-disable-next-line
  }, []);

  const modalRef = useRef(null);
  const refClose = useRef(null)
  const ref = useRef(null);
  const [note, setNote] = useState({id : "", etitle: "", edescription: "", etag: "" });

 const updateNote = (currentNote) => {
      setNote({ // Set the state
        id: currentNote._id,
        etitle: currentNote.title,
        edescription: currentNote.description,
        etag: currentNote.tag,
      });
      ref.current.click(); // Open the modal immediately
    };

  const handleClick = (id) => { // Accept id as an argument
  
  editNote(id, note.etitle, note.edescription, note.etag);
  refClose.current.click();
};

  const handleClose = () => {
    if (modalRef.current) {
    const modalInstance = window.bootstrap.Modal.getInstance(modalRef.current);
    if (modalInstance) {
      modalInstance.hide();
    }
  }
  };


  const onChange = (e) => {
    setNote({ ...note, [e.target.name]: e.target.value });
  };

  return (
    <>
      <AddNote />
      <button ref={ref} type="button" className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModalCenter">
        Launch demo modal
      </button>


      <div className="modal fade" id="exampleModalCenter" tabIndex="-1" role="dialog" ref={modalRef} aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLongTitle">Edit Note</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close">

                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              <form className='my-3'>
                <div className="form-group">
                  <label htmlFor="title">Title</label>
                  <input type="text" className="form-control" id="etitle" name='etitle' value={note.etitle || ""} aria-describedby="emailHelp" onChange={onChange} />
                </div>
                <div className="form-group">
                  <label htmlFor="description">Description</label>
                  <input type="text" className="form-control" id="edescription" name='edescription' value={note.edescription || ""} onChange={onChange} />
                </div>
                <div className="form-group">
                  <label htmlFor="tag">Tag</label>
                  <input type="text" className="form-control" id="etag" name='etag' value={note.etag || ""} onChange={onChange} />
                </div>
                
              </form>
            </div>
            <div className="modal-footer">
              <button type="button" ref={refClose} className="btn btn-secondary" onClick={handleClose} data-dismiss="modal">Close</button>
              <button
  type="button"
  onClick={() => handleClick(note.id)} // Pass the id here
  className="btn btn-primary"
>
  Update Note
</button>
            </div>
          </div>
        </div>
      </div>
      <div className='row my-3'>
        <h2>Your notes</h2>
        {notes.length === 0 ? (
          <p>No notes to display</p>
        ) : (
          notes.map((note) => (
            <Noteitem key={note._id} updateNote={updateNote} note={note} />
          ))
        )}
      </div>
    </>
  );
};

export default Notes;
