import React, { useState } from 'react'

const Login = () => {

    const [cradentionals, setcradentionals] = useState({ email: "", password: "" })

    const handlesubmit = async (e) => {
        e.preventDefault();
        const responce = await fetch(`http://localhost:5000/api/auth/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email: cradentionals.email, password: cradentionals.password })
        });
        const json = await responce.json();
        if (json.success) {
            //save the auth token and redirect
            localStorage.setItem('token', json.authtoken)
        } else {
            alert("invalid cradentials")
        }
        console.log(json)
    };

    const onChange = (e) => {
        setcradentionals({ ...cradentionals, [e.target.name]: e.target.value });
    };

    return (
        <div>
            <form onSubmit={handlesubmit}>
                <div className="form-group">
                    <label htmlFor="email">Email address</label>
                    <input type="email" className="form-control" onChange={onChange} value={cradentionals.email} id="email" name='email' aria-describedby="emailHelp" />
                    <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input type="password" className="form-control" onChange={onChange} value={cradentionals.password} name='password' id="password" />
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

export default Login
