import React, { useContext, useEffect, useRef } from 'react';
import noteContext from '../Context/notes/noteContext';
import Noteitem from './Noteitem';
import AddNote from './AddNote';


const Notes = () => {
  const context = useContext(noteContext);
  const { notes, getallNotes } = context;

  const ref = useRef(null);

  useEffect(() => {
    getallNotes();
    // eslint-disable-next-line
  }, []);

  const updateNote = () => {
    const modal = new window.bootstrap.Modal(ref.current);
    modal.show();
  };

  return (
    <>
      <AddNote />

      <div className="modal fade" id="exampleModalCenter" tabIndex="-1" role="dialog" ref={ref} aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLongTitle">Edit Note</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              ...
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" className="btn btn-primary">Save changes</button>
            </div>
          </div>
        </div>
      </div>

      <div className='row my-3'>
        <h2>Your notes</h2>
        {notes.length === 0 ? (
          <p>No notes to display</p>
        ) : (
          notes.map((note) => (
            <Noteitem key={note._id} updateNote={updateNote} note={note} />
          ))
        )}
      </div>
    </>
  );
};

export default Notes;
