import { Link, useLocation, useNavigate } from "react-router-dom";

const Navbar = () => {
  let location = useLocation();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/Login");
  };

  const isLoggedIn = !!localStorage.getItem("token");

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-gradient shadow-sm sticky-top">
      <div className="container-fluid">
        {/* Brand */}
        <Link className="navbar-brand fw-bold fs-3 text-light" to={isLoggedIn ? "/home" : "/Login"}>
          <i className="bi bi-journal-text me-2"></i> iNotebook
        </Link>

        {/* Toggler for mobile */}
        <button
          className="navbar-toggler border-0"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Nav Links */}
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            {isLoggedIn && (
              <>
                <li className="nav-item">
                  <Link
                    className={`nav-link px-3 fw-semibold ${
                      location.pathname === "/home" ? "active-nav" : "text-light"
                    }`}
                    to="/home"
                  >
                    Home
                  </Link>
                </li>
                <li className="nav-item">
                  <Link
                    className={`nav-link px-3 fw-semibold ${
                      location.pathname === "/about" ? "active-nav" : "text-light"
                    }`}
                    to="/about"
                  >
                    About
                  </Link>
                </li>
              </>
            )}
          </ul>

          {/* Auth Buttons */}
          <div className="d-flex">
            {!isLoggedIn ? (
              <>
                <Link className="btn btn-outline-light rounded-pill mx-2 px-4 fw-semibold" to="/Login">
                  Login
                </Link>
                <Link className="btn btn-warning rounded-pill mx-2 px-4 fw-semibold" to="/Signup">
                  Sign Up
                </Link>
              </>
            ) : (
              <button
                className="btn btn-danger rounded-pill mx-2 px-4 fw-semibold"
                onClick={handleLogout}
              >
                Logout
              </button>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
