import React, {useContext} from 'react'
import noteContext from '../Context/notes/noteContext';

const Noteitem = (props) => {
  const Context = useContext(noteContext)
  const {deleteNote}= Context;
  const { note, updateNote } = props;
  return (
    <div className='col-md-3'>
      <div className="card my-3" >
        <div className="card-body">
          <div className='d-flex align-items-center'>
            <h5 className="card-title">{note.title}</h5>
            <i className="fa-solid fa-trash mx-2 cursor-pointer" onClick={()=>{deleteNote(note._id);  props.showAlert("Note deleted successfully", "Success")}}></i>
            <i className="fa-solid fa-pen-to-square mx-2 cursor-pointer" onClick={() => { updateNote(note) } }></i>
          </div>
          <p className="card-text">{note.description}</p>
        </div>
      </div>
    </div>
  )
}

export default Noteitem;
